theme_scss:
.pos {
    display: flex;
    flex-flow: row nowrap;
    min-height: calc(100vh - 112px);
}

.pos-cart {
    height: 100%;
    display: flex;
    flex-flow: column nowrap;
}

.pos-cart > .cart-wrapper {
    height: 100%;
}

.pos-cart > .cart-wrapper > .list-item-table {
    height: 100%;
    display: flex;
    flex-flow: column nowrap;
}

.pos-cart > .cart-wrapper > .list-item-table > .cart-items {
    flex: 1 1 auto;
}

.item-container {
    display: flex;
    flex-flow: column nowrap;
}

.item-container > .items-wrapper {
    height: 100%;
}
